import "./Stylesheets/index.css";
import EventTile from "./EventTile";
import placeholderImage from "./images/placeholderImage.png";
import Dropdown from "./Components/Dropdown";
import FormDelegate from "./FormDelegate";
import EventPopup from './Components/EventPopup'

class EventBrowser extends FormDelegate {
  constructor(props) {
    super(props);

    this.blankEvent = {
      attendees: [],
      capacity: "",
      categories: [],
      date: "",
      description: "",
      eventID: -1,
      location: "",
      name: "",
      owner: -1
    };

    this.state = {
      menuType: "browser",
      popUpInfo: {
        thumbnail: placeholderImage,
        event: this.blankEvent
      }
    }

    this.browser = this.browser.bind(this);
    this.form = this.form.bind(this);
    this.openForm = this.openForm.bind(this);
    this.closeForm = this.closeForm.bind(this);
    this.getContent = this.getContent.bind(this);


    this.categoryOptions = this.props.categories;
  }

  openForm(thumb, eventObj, creating) {
    this.setState({
      menuType: "form",
      popUpInfo: {
        thumbnail: thumb,
        event: eventObj,
        creating: creating,
      }
    });
    //console.log(`${thumb} ${title} ${owner}`);
  }

  closeForm() {
    this.setState({ menuType: "browser" });
    //console.log('return');
  }

  submitData() {
    return;
  }

  render() {
    return (
      <div className="event-browser-container">
        <h1 className="primary-header">Campus Events Near Me</h1>
        <div className="event-tile-browser-container">
          {this.getContent()}
        </div>
        <div className="event-browser-bottom-buttons">
          <Dropdown
            label="Filter"
            isSecure={false}
            showLabel={false}
            options={["No Filter"].concat(this.categoryOptions)}
            fields={this.fields}
          />
          <button className="new-event-button" onClick={() => this.openForm("", this.blankEvent, true)}>New Event</button>
        </div>
      </div>
    );
  }

  getContent() {
    switch (this.state.menuType) {
      case "form":
        return this.form();
      default:
        return this.browser();
    }
  }

  browser() {
    //return (this.generateFillerTiles(25));
    let tiles = [];
    for (let i = 0; i < this.props.events.length; i++) {
      let e = this.props.events[i];
      console.log(e);
      tiles.push(
        <EventTile
          thumbnail={e.thumbnail || placeholderImage}
          key={i}
          id={i}
          title={e.name}
          owner={e.ownerName}
          creating={false}
          onClick={this.openForm.bind(this, e.thumbnail || placeholderImage, e, false)}
        />
      );
    }
    return tiles;
  }

  generateFillerTiles(count) {
    let tiles = [];
    for (let i = 0; i < count; i++) {
      tiles.push(
        <EventTile
          thumbnail={placeholderImage}
          key={i}
          id={i}
          title="Event Name"
          owner="Owner Name"
          creating={false}
          onClick={this.openForm}
        />
      );
    }
    return tiles;
  }

  form() {
    return (
      <EventPopup
        thumbnail={this.state.popUpInfo.thumbnail}
        event={this.state.popUpInfo.event}
        creating={this.state.popUpInfo.creating}
        onClick={this.closeForm}
        currentUser={this.props.currentUser}
        categories={this.categoryOptions}
      />
    );
  }
}

export default EventBrowser;
