import { Component } from "react";
import arrow from "../images/back-arrow.png";

class EventPopup extends Component {
  constructor(props) {
    super(props);

    this.sendEvent = this.sendEvent.bind(this);
    this.getContent = this.getContent.bind(this);
    this.creating = this.creating.bind(this);
    this.editing = this.editing.bind(this);
    this.getEventTitle = this.getEventTitle.bind(this);
    this.getEventOwner = this.getEventOwner.bind(this);
    this.getSuggestions = this.getSuggestions.bind(this);
    this.getDescriptionField = this.getDescriptionField.bind(this);
    this.getDateField = this.getDateField.bind(this);
    this.getLocationField = this.getLocationField.bind(this);
    this.getCapacityField = this.getCapacityField.bind(this);
    this.getCategoriesField = this.getCategoriesField.bind(this);
    this.state = { value: "" };
    this.onChange = this.onChange.bind(this);
  }

  sendEvent(event) {
    try {
      event.preventDefault();
      // console.log(event.target);
      const field = [
        "name",
        "owner",
        "thumbnail",
        "description",
        "date",
        "location",
        "capacity",
        "categories",
      ];
      let obj = {};
      for (let i = 0; i < event.target.children.length - 1; i++) {
        let child = event.target.children[i];
        console.log(child.children[1].value);
        obj[field[i]] = child.children[1].value;
      }
      obj["categories"] = obj["categories"].split(",");
      console.log(obj);
      fetch("http://localhost:8080/api/add-event", {
        method: "POST",
        mode: "cors",
        cache: "no-cache",
        credentials: "same-origin",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(obj),
      });
      this.props.onClick();
    } catch (e) {
      console.log(e);
    }
  }

  render() {
    return (
      <div className="event-popup" onSubmit={this.sendEvent}>
        <div className="event-popup-header">
          <img
            className="back-arrow"
            src={arrow}
            alt=""
            onClick={this.props.onClick}
          />
          <h1 className="event-title">{this.getEventTitle()}</h1>
        </div>
        {this.getEventOwner()}
        {this.getContent()}
      </div>
    );
  }

  getEventTitle() {
    if (this.props.creating) {
      return "Create New Event";
    }
    return this.props.title;
  }

  getEventOwner() {
    if (this.props.creating) {
      return <div></div>;
    }
    return <p className="event-owner">{this.props.owner}</p>;
  }

  getContent() {
    if (this.props.creating) {
      return this.creating();
    } else {
      return this.editing();
    }
  }

  getSuggestions(categories) {
    let options = [];
    for (let o of this.props.categories) {
      if (categories && categories.includes(o)) {
        options.push(
          <option value={o} selected>
            {o}
          </option>
        );
      } else {
        options.push(<option value={o}>{o}</option>);
      }
    }
    return (
      <select id="suggestions" multiple>
        {options}
      </select>
    );
  }

  getDescriptionField(text) {
    return (
      <div className="event-form-entry">
        <label htmlFor="form-event-description">Description: </label>
        <textarea
          type="text"
          className="form-input"
          id="form-event-description"
          name="form-event-description"
          rows="1"
          defaultValue={text}
        ></textarea>
      </div>
    );
  }

  getDateField(text) {
    return (
      <div className="event-form-entry">
        <label htmlFor="form-event-date">Date: </label>
        <input
          type="date"
          className="form-input"
          id="form-event-date"
          name="form-event-date"
          placeholder="YYYY-MM-DD-HH:MM"
          defaultValue={text}
        ></input>
      </div>
    );
  }

  handleChange(e) {
    const regex =
      /^(19|20)\d\d\-(0[1-9]|1[012])\-(0[1-9]|[12][0-9]|3[01])\-(0[0-9]|1[0-9]|2[0-3]):[0-5][0-9]$/;
    if (e.target.value === "" || regex.test(e.target.value)) {
      this.setState({ value: e.target.todo });
    }
  }

  getLocationField(text) {
    return (
      <div className="event-form-entry">
        <label htmlFor="form-event-location">Location: </label>
        <input
          type="text"
          className="form-input"
          id="form-event-location"
          name="form-event-location"
          defaultValue={text}
        ></input>
      </div>
    );
  }

  getCapacityField(text) {
    return (
      <div className="event-form-entry">
        <label htmlFor="form-event-capacity">Capacity: </label>
        <input
          value={this.state.value}
          onChange={this.onChange}
          className="form-input"
          id="form-event-capacity"
          name="form-event-capacity"
          defaultValue={text}
        ></input>
      </div>
    );
  }

  onChange(e) {
    const re = /^[0-9\b]+$/;
    if (e.target.value === "" || re.test(e.target.value)) {
      this.setState({ value: e.target.value });
    }
  }

  getCategoriesField(categories) {
    return (
      <div className="event-form-entry">
        <label htmlFor="form-event-categories">Categories: </label>
        {this.getSuggestions(categories)}
      </div>
    );
  }

  creating() {
    return (
      <form className="event-popup-form">
        {/* hidden input setting owner equal to the user */}
        <div className="event-form-entry">
          <label htmlFor="form-event-title">Event Name: </label>
          <input
            type="text"
            className="form-input"
            id="form-event-title"
            name="form-event-title"
          ></input>
        </div>
        <div>
          {/* placeholder div so the input is the second child */}
          <div></div>
          <input type="hidden" value={this.props.currentUser}></input>
        </div>
        <div className="event-form-entry">
          <label htmlFor="event-thumbnail-input">Upload Event Thumbnail</label>
          <input
            type="file"
            className="event-thumbnail-input"
            name="event-thumbnail-input"
            accept="image/png, image/jpeg"
          ></input>
        </div>
        {this.getDescriptionField("")}
        {this.getDateField("")}
        {this.getLocationField("")}
        {this.getCapacityField("")}
        {this.getCategoriesField("")}

        <input type="submit" className="event-submit"></input>
      </form>
    );
  }

  editing() {
    return (
      <form className="event-popup-form">
        <div>
          <div></div>
          <input
            type="hidden"
            value={this.props.event.name}
            className="form-event-title"
          ></input>
        </div>
        <div>
          <div></div>
          <input
            type="hidden"
            value={this.props.event.owner}
            className="form-event-owner"
          ></input>
        </div>
        <div>
          <div></div>
          <img src={this.props.thumbnail} alt="" className="event-thumbnail" />
        </div>
        {this.getDescriptionField(this.props.event.description)}
        {this.getDateField(this.props.event.date)}
        {this.getLocationField(this.props.event.location)}
        {this.getCapacityField(this.props.event.capacity)}
        {this.getCategoriesField(this.props.event.categories)}

        <input type="submit" className="event-submit"></input>
      </form>
    );
  }
}

export default EventPopup;
